[solution]
code = '''
class PositiveInt:
    def __init__(self):
        self.name = None

    def __set_name__(self, owner, name):
        self.name = name

    def __get__(self, instance, owner):
        if instance is None:
            return self
        return instance.__dict__.get(self.name)

    def __set__(self, instance, value):
        if not isinstance(value, int) or value <= 0:
            raise ValueError("must be positive")
        instance.__dict__[self.name] = value

class EmailStr:
    def __init__(self):
        self.name = None

    def __set_name__(self, owner, name):
        self.name = name

    def __get__(self, instance, owner):
        if instance is None:
            return self
        return instance.__dict__.get(self.name)

    def __set__(self, instance, value):
        if "@" not in value:
            raise ValueError("must contain @")
        if "." not in value:
            raise ValueError("must contain .")
        instance.__dict__[self.name] = value

class RangeInt:
    def __init__(self, min_val, max_val):
        self.min_val = min_val
        self.max_val = max_val
        self.name = None

    def __set_name__(self, owner, name):
        self.name = name

    def __get__(self, instance, owner):
        if instance is None:
            return self
        return instance.__dict__.get(self.name)

    def __set__(self, instance, value):
        if not (self.min_val <= value <= self.max_val):
            raise ValueError(f"must be between {self.min_val} and {self.max_val}")
        instance.__dict__[self.name] = value

def solution(test_spec):
    validator_name = test_spec["validator"]
    value = test_spec["value"]

    # Create test class with validator
    if validator_name == "PositiveInt":
        class TestClass:
            field = PositiveInt()
    elif validator_name == "EmailStr":
        class TestClass:
            field = EmailStr()
    elif validator_name == "RangeInt":
        min_val = test_spec["min"]
        max_val = test_spec["max"]
        class TestClass:
            field = RangeInt(min_val, max_val)

    # Test validation
    obj = TestClass()
    try:
        obj.field = value
        return {"status": "valid", "stored_value": obj.field}
    except ValueError as e:
        return {"status": "invalid", "error": str(e)}
'''

