python3 -c "
import tomllib
from pathlib import Path

concepts_dir = Path('concepts')
errors = []

for level in ['level_3', 'level_4']:
    level_dir = concepts_dir / level
    for toml_file in level_dir.glob('*.toml'):
        try:
            with open(toml_file, 'rb') as f:
                data = tomllib.load(f)
                # Check required fields
                if 'concept' not in data:
                    errors.append(f'{toml_file}: Missing [concept] section')
                else:
                    required = ['id', 'name', 'level', 'category', 'prerequisites', 'unlocks']
                    for field in required:
                        if field not in data['concept']:
                            errors.append(f'{toml_file}: Missing concept.{field}')
                if 'description' not in data:
                    errors.append(f'{toml_file}: Missing [description] section')
                print(f'✓ {toml_file.name}')
        except Exception as e:
            errors.append(f'{toml_file}: {e}')
            print(f'✗ {toml_file.name}: {e}')

if errors:
    print('\nErrors found:')
    for error in errors:
        print(f'  - {error}')
else:
    print('\n✓ All concept files are valid!')
"
